import { Subject } from 'rxjs/Subject';
import { Observable } from 'rxjs/Observable';
import { of } from 'rxjs/observable/of';
import 'rxjs/add/operator/expand';
import 'rxjs/add/operator/delay';

export interface IAdEvent {
    type: 'IMAGE' | 'VIDEO',
    src: string,
    name: string
}

interface ICreative {
    name: string;
    src: string;
}

const IMAGE_CREATIVES: ICreative [] = [
    {
        name: 'ray ban',
        src: 'data:image/jpeg;base64,'
    },

];

export class AdEmitter {
    private _adEmitter$ = new Subject<IAdEvent>();

    constructor() {
        this.startEmissions();
    }

    private getRandomAdd(): IAdEvent {
        const isRandomImage = Math.random() > 0.5;

        if (true || isRandomImage) { // todo
            const randomImageCreativeIndex = Math.floor(Math.random() * IMAGE_CREATIVES.length);
            const randomImageCreative = IMAGE_CREATIVES[randomImageCreativeIndex];
            return {
                ...randomImageCreative,
                type: 'IMAGE'
            };
        }
    }

    private startEmissions(): void {
        of(null).expand(() => {
            const randomDelay = Math.round(Math.random() * 5000);
            return of(this.getRandomAdd())
                .delay(randomDelay);
        })
            .subscribe((adEvent) => {
                this._adEmitter$.next(adEvent);
            });
    }

    get adEmitter$(): Observable<IAdEvent> {
        return this._adEmitter$.asObservable();
    }

    registerToAdEvents(cb: (adEvent: IAdEvent) => void): { removeListener: () => void } {
        const sub = this.adEmitter$
            .subscribe((evt) => {
                cb(evt);
            });

        return {
            removeListener: () => {
                sub.unsubscribe();
            }
        };
    }
}